import React, { Component, Fragment } from 'react'
import { connect } from 'react-redux'

import { fetchBeerById } from 'actions'
import { getBeerById } from 'selectors'

class Modal extends Component {
    //Fetching a single beer from API, according to the id in URL
    componentDidMount () {
        this.props.fetchBeerById(this.props.match.params.id)
    }

    //Rendering a content panel which shows the decription
    renderContent (beer) {
        return (
            <Fragment>
                <h4 className='name'>
                    {beer.name}
                </h4>
                <h5 className='tagline'>
                    {beer.tagline}
                </h5>
                <div className='line'></div>
                <div className='percentage-container'>
                    <p className='percentage'>
                        <strong>ABV: </strong>{beer.abv}%
                    </p>
                    <p className='percentage'>
                        <strong>IBU: </strong>{beer.ibu}
                    </p>
                    <p className='percentage'>
                        <strong>EBC: </strong>{beer.ebc}
                    </p>
                </div>
                <p className='text'>{beer.description}</p>
                <div className='food'>
                    <strong>Best served with:</strong>
                    {beer.food_pairing.map((food, index) => 
                        <p key={index} className='text'>-{food}</p>
                    )}
                </div>
            </Fragment>
        )
    }

    render () {
        const {beer} = this.props
        //Func that allows on clicking outside of the modal in order to close it
        const back = e => {
            e.stopPropagation();
            this.props.history.goBack();
        }
        //Func that prevents click on modal
        const stop = e => {
            e.stopPropagation();
        }
        return (
            <div className='modal-container' onClick={back}>
                <div className='modal container' onClick={stop}>
                    <div className='row'>
                        <div className='col-md-3'>
                            {beer && <img className='img-thumbnail-details' src={beer.image_url} alt={beer.name} />}
                        </div>
                        <div className='col-md-9'>
                            {beer && this.renderContent(beer)}
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    beer: getBeerById(state, state.beerModal.id)
})

const mapDispatchToProps = {
    fetchBeerById
}

export default connect(mapStateToProps, mapDispatchToProps)(Modal)