import * as R from 'ramda'

import {
    FETCH_BEERS_SUCCESS,
    LOAD_MORE_BEERS_SUCCESS,
    FETCH_BEER_BY_ID_SUCCESS
} from 'actionTypes'

const initialState = {}

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_BEERS_SUCCESS:
            //Assign an object with data and 'id' will be admited as the key
            const newValues = R.indexBy(R.prop('id'), payload)
            //Adding new values or creating them (in case there has been nothing before)
            return R.merge(state, newValues)
        case LOAD_MORE_BEERS_SUCCESS:
            const moreValues = R.indexBy(R.prop('id'), payload)
            return R.merge(state, moreValues)
        case FETCH_BEER_BY_ID_SUCCESS:
            //Add (or rewrite) the data to the state and put id as the key
            return R.assoc(payload.id, payload, state)
        default:
            return state
    }
}