import * as R from 'ramda'

export const getBeerById = (state, id) => R.prop(id, state.beers)

//Preparing data to be available to be used in a component
export const getBeers = state => {
    const beers = R.map(id => getBeerById(state, id), state.beersPage.ids)
    return beers
}

//Giving a length of the array (Beers that are already rendered on the page)
export const getRenderedBeersLength = state => R.length(state.beersPage.ids)