import {
    FETCH_BEERS_START,
    FETCH_BEERS_SUCCESS,
    FETCH_BEERS_FAILURE
} from 'actionTypes'
import { fetchBeers as fetchBeersApi } from 'api'

export const fetchBeers = () => async dispatch => {
    dispatch({type: FETCH_BEERS_START})

    try {
        const beers = await fetchBeersApi()
        dispatch({
            type: FETCH_BEERS_SUCCESS,
            payload: beers
        })
    } catch (err) {
        dispatch({
            type: FETCH_BEERS_FAILURE,
            payload: err,
            error: true
        })
    }
    
}