import * as R from 'ramda'

import {
    FETCH_BEER_BY_ID_SUCCESS
} from 'actionTypes'

const initialState = {
    id: null
}

//Get id from payload
export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_BEER_BY_ID_SUCCESS:
            return R.merge(state, {
                id: R.prop('id', payload)
            })
        default:
            return state
    }
}